import React from "react";
import "../../Styles/Shared/Navbar.scss";
import logo from "../../Assets/svg/logo.svg";
import SearchIcon from "@material-ui/icons/Search";
import MenuRoundedIcon from "@material-ui/icons/MenuRounded";
import { useState } from "react";
import SolutionsMenu from "./SolutionsMenu";
import ProductsMenu from "./ProductsMenu";

function Navbar() {
  const [scroll, setScroll] = useState(false);
  const [solutionsMenu, setSolutionsMenu] = useState(false);

  const changeBackground = () => {
    if (window.scrollY >= 100) {
      setScroll(true);
    } else {
      setScroll(false);
    }
  };

  window.addEventListener("scroll", changeBackground);
  return (
    <>
      <div
        className='solutionsMegaMenu'
        style={{ display: solutionsMenu ? "block" : "" }}
        onMouseLeave={() => {
          setSolutionsMenu(!solutionsMenu);
        }}>
        <SolutionsMenu />
      </div>
      <div className='productsMegaMenu'>
        <ProductsMenu />
      </div>
      <div className={scroll ? "navbarActive navbar" : "navbar"}>
        <div className='navbarLogo'>
          <div className='logo'>
            <img src={logo} alt='' />
          </div>
          <h2>
            Pay<span>2Gate</span>
          </h2>
        </div>
        <div className='navbarList'>
          <ul>
            <li>Products</li>
            <li onMouseOver={() => setSolutionsMenu(!solutionsMenu)}>
              Solutions
            </li>
            <li>Features</li>
            <li>Pricing</li>
            <li>FAQ</li>
            <li>Contact</li>
            <li>
              <SearchIcon />
            </li>
          </ul>
        </div>
        <div className='navbarButtons'>
          <button>Login</button>
          <button>Signup</button>
        </div>
        <div className='burgerIcon'>
          <MenuRoundedIcon />
        </div>
      </div>
    </>
  );
}

export default Navbar;
