import React from "react";
import "../../Styles/Shared/HomeCard.scss";
import MyButton from "../../Components/Shared/MyButton";
import ShoppingCartIcon from "@material-ui/icons/ShoppingCart";

function HomeCard({ title, paragraf, oldPrice, price, bgImage }) {
  return (
    <div className='homeCard' style={{ backgroundImage: `url(${bgImage})` }}>
      <h4>{title}</h4>
      <p>{paragraf}</p>
      <div className='homeCard__price'>
        <span className='old-price'>{oldPrice}$</span>
        <h5>{price}$</h5>
      </div>
      <MyButton icon={<ShoppingCartIcon />} name='Buy now' />
    </div>
  );
}

export default HomeCard;
