import React, { useState, useEffect, useRef } from "react";
import "../../Styles/Shared/Navbar.scss";
import logo from "../../Assets/svg/logo.svg";
import ArrowDropDownIcon from "@material-ui/icons/ArrowDropDown";
import ShoppingCartOutlinedIcon from "@material-ui/icons/ShoppingCartOutlined";
import SearchOutlinedIcon from "@material-ui/icons/SearchOutlined";
import MenuOpenOutlinedIcon from "@material-ui/icons/MenuOpenOutlined";
import { Link, useHistory } from "react-router-dom";
import SupportMenu from "./SupportMenu";
import GamesMenu from "./GamesMenu";
import CartMenu from "./CartMenu";
import { disableBodyScroll, enableBodyScroll } from "body-scroll-lock";
import NavbarResp from "./NavbarResp";
import CloseIcon from "@material-ui/icons/Close";

function Navbar() {
  let history = useHistory();
  const [showSupportMenu, setShowSupportMenu] = useState(false);
  const [showGamesMenu, setShowGamesMenu] = useState(false);
  const [showCart, setShowCart] = useState(false);
  const [showNavbar, setShowNavbar] = useState(false);
  const wrapperRef = useRef(null);

  useEffect(() => {
    function handleClickOutside(event) {
      if (wrapperRef.current && !wrapperRef.current.contains(event.target)) {
        setShowNavbar(showNavbar);
      }
    }
    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, [wrapperRef]);
  showNavbar ? disableBodyScroll(document) : enableBodyScroll(document);
  return (
    <>
      <div
        className='supportMegaMenu'
        style={{ display: showSupportMenu ? "block" : "" }}
        onMouseLeave={() => setShowSupportMenu(!showSupportMenu)}>
        <SupportMenu />
      </div>
      <div
        className='gamesMegaMenu'
        style={{ display: showGamesMenu ? "block" : "" }}
        onMouseLeave={() => setShowGamesMenu(!showGamesMenu)}>
        <h4>Games</h4>
        <GamesMenu />
      </div>
      <div
        className='shoppingCart'
        style={{ transform: showCart ? "translateX(0)" : "" }}>
        <CartMenu closeFunction={() => setShowCart(!showCart)} />
      </div>
      <div
        ref={wrapperRef}
        className='mobileMenu'
        style={{ transform: showNavbar ? "translateX(0)" : "" }}>
        <NavbarResp />
      </div>
      <div className='navbar'>
        <div className='navbar__content'>
          <div className='navbar__logo'>
            <Link to='/'>
              <img src={logo} alt='' />
              <h3>Battle Games</h3>
            </Link>
          </div>

          <div className='navbar__list'>
            <ul>
              <li>Home</li>
              <li onMouseOver={() => setShowGamesMenu(!showGamesMenu)}>
                Games <ArrowDropDownIcon />
              </li>
              <li>
                <Link to='/shop'>Shop</Link>
              </li>
              <li>
                <Link to='/about'>About</Link>
              </li>
              <li>Blog</li>
              <li onClick={() => setShowSupportMenu(!showSupportMenu)}>
                Support <ArrowDropDownIcon />
              </li>
            </ul>
          </div>
          <div className='navbar__right'>
            <ShoppingCartOutlinedIcon onClick={() => setShowCart(!showCart)} />
            <Link to='/search'>
              <SearchOutlinedIcon />
            </Link>

            <button
              className='loginButton'
              onClick={() => history.push("/login")}>
              Log in
            </button>
            <button
              className='signupButton'
              onClick={() => history.push("/signup")}>
              SignUp
            </button>
          </div>
          <div className='burgerIcon'>
            <MenuOpenOutlinedIcon
              style={{
                display: showMenu ? "none" : "",
                transition: "0.2s linear",
              }}
              onClick={() => setShowNavbar(!showNavbar)}
            />
            <CloseIcon
              style={{
                display: showMenu ? "" : "none",
                transition: "0.2s linear",
              }}
              onClick={() => setShowNavbar(!showNavbar)}
            />
          </div>
        </div>
      </div>
    </>
  );
}

export default Navbar;
