import React, { useState } from "react";
import "../../Styles/Shared/CartMenu.scss";
import CloseIcon from "@material-ui/icons/Close";
import MyButton from "./MyButton";
import cartItem1 from "../../Assets/cartItem1.png";
import cartItem2 from "../../Assets/cartItem2.png";
import DeleteForeverOutlinedIcon from "@material-ui/icons/DeleteForeverOutlined";
import { IconButton } from "@material-ui/core";
import CheckoutProduct from "./CheckoutProduct";
import { useStateValue } from "../../Redux/StateProvider";

function CartMenu({ closeFunction }) {
  const [quantity, setQuantity] = useState(1);
  const [quantity2, setQuantity2] = useState(1);

  const [{ basket }] = useStateValue();
  return (
    <div className='cartMenu'>
      <div className='cartMenu__header'>
        <h4>Your Shopping Cart</h4>
        <CloseIcon onClick={closeFunction} />
      </div>

      {basket?.length === 0 ? (
        <h2>Shopping basket is empty</h2>
      ) : (
        <div>
          {basket?.map((item) => (
            <CheckoutProduct
              id={item.id}
              title={item.title}
              image={item.image}
              price={item.price}
            />
          ))}
        </div>
      )}
    </div>
  );
}

export default CartMenu;

