import React from "react";
import "../../Styles/Shared/Footer.scss";
import { ReactComponent as Facebook } from "../../Assets/Svg/facebook.svg";
import { ReactComponent as Instagram } from "../../Assets/Svg/instagram.svg";
import { ReactComponent as Linkedin } from "../../Assets/Svg/linkedin.svg";
import { ReactComponent as Twitter } from "../../Assets/Svg/twitter.svg";
import { ReactComponent as Twitter2 } from "../../Assets/Svg/twitter2.svg";
import { ReactComponent as Phone } from "../../Assets/Svg/smartphone.svg";
import { Link } from "react-router-dom";
import footerLinks from "./footerLinks";
import Select from "../Shared/Select";
import languages from "../../translations/languages";
import dataLocalStorage from "../../dataLocalStorage";
const Footer = (props) => {
  const languagesArray = Object.keys(languages).map((key) => languages[key]);
  console.log("--->", languagesArray);
  const selectedLanguage = languages[props.locale];
  console.log("+++", selectedLanguage);
  return (
    <footer className='footer'>
      <div className='container'>
        <div className='footerText'>
          <h2>Don't waste time to reach your goals!</h2>
          <p>Join over 50.000 happy users now.</p>
          <button className='button start-now-btn'>Start Now</button>
        </div>
        <div className='divider'></div>
        <div className='footer-links'>
          {footerLinks.map((l) => (
            <LinkCategory key={l.category} {...l} />
          ))}
        </div>
        <div className='subscribe'>
          <p>Don't waste time to reach your goals!</p>
          <div className='subscribeEmail'>
            <input type='email' placeholder='Enter your email address' />
            <button className='button'>Subscribe</button>
          </div>
        </div>
        <div className='divider'></div>
        <div className='languageSwitcher'>
          <div className='right'>
            <span className='phone'>
              <a href='tel:+08002525522255'>
                <Phone /> +0800 252 552 22 55
              </a>
            </span>
            <Select
              selected={{
                value: selectedLanguage.value,
                label: selectedLanguage.label,
              }}
              icon={<selectedLanguage.flag />}
              handleChange={(obj) => {
                props.setLocale({ value: obj.value, label: obj.label });
                dataLocalStorage.setLocale(obj.value);
              }}
              styles='drop-up'>
              {languagesArray.map((lang) => (
                <div key={lang.value} label={lang.label} value={lang.value}>
                  <lang.flag />
                  {lang.label}
                </div>
              ))}
            </Select>
          </div>
        </div>
        <div className='divider'></div>
        <div className='copyright'>
          <div className='left'>
            <p className='paragraph-3'>
              &copy; 2020 Pay2Gate. All rights reserved.
            </p>
            <div className='links'>
              <Link to='/privacy' className='border-sides'>
                Privacy Policy
              </Link>
              <Link to='/terms-conditions'>Terms & Conditions</Link>
            </div>
          </div>
          <div className='right'>
            <div className='social-media-links'>
              <a
                className='social-media-link'
                href='http://facebook.com'
                target='_blank'
                rel='noopener noreferrer'>
                <Facebook />
              </a>
              <a
                className='social-media-link'
                href='http://instagram.com'
                target='_blank'
                rel='noopener noreferrer'>
                <Instagram />
              </a>
              <a
                className='social-media-link'
                href='http://twitter.com'
                target='_blank'
                rel='noopener noreferrer'>
                <Twitter2 />
              </a>
              <a
                className='social-media-link'
                href='http://linkedin.com'
                target='_blank'
                rel='noopener noreferrer'>
                <Linkedin />
              </a>
              <a
                className='social-media-link'
                href='http://twitter.com'
                target='_blank'
                rel='noopener noreferrer'>
                <Twitter />
              </a>
            </div>
          </div>
        </div>
      </div>
    </footer>
  );
};

const LinkCategory = (props) => {
  return (
    <div className='link-category'>
      <span className='paragraph-3'>{props.category}</span>

      <div className='links'>
        {props.links.map((l) => {
          return (
            <Link key={l.to} to={l.to} className='footer-link'>
              {l.link}
            </Link>
          );
        })}
      </div>
    </div>
  );
};

export default Footer;
